import React, { useState } from 'react';
import '../mycss/Header.css'; 
import myphoto from '../assets/turam.jpg';
import resume from '../assets/Resume.jpg'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleMenuClick = () => {
    setMenuOpen(false); // Close the menu when a menu item is clicked
  };

  return (
    <header>
      <div className="container">
        <div className='box first-box'>
          <img src={myphoto} alt="Portrait of Jayce Turambe" width="350" />
          <div className="text">
            <h2>Full-Stack Developer</h2>
            <p>Jayce Turambe</p>
            <p>
              üñêüèª I'm a front-end enthusiast on a mission, passionate about React.js & Next.js and comfortable with Node.js, Express.js, & MongoDB.
            </p>
          </div>
          <div className='external-link-wrapper'>
            <a href="#profile" onClick={handleMenuClick} className="external-link">
              <FontAwesomeIcon icon={faExternalLinkAlt} className='icon'/>
            </a>
          </div>

        </div>
        <div className='box'>
          <div className="resume-preview">
            {/* Display a preview image of my resume */}
            <img src={resume} alt="Resume Preview" />
          </div>
          <div className="resume-download">
            {/* Provide a link to download the PDF */}
            <a href="https://drive.google.com/file/d/1jtxWKFS4JZ6ZkxJHyLsGQ5_r7Hf0jZe-/view?usp=sharing" target="_blank" rel="noopener noreferrer">View Resume</a>
          </div>
        </div>
        <div className='box'>

        </div>

        <div className='hamburgermenu'>
          <button className='hamburger-button' onClick={toggleMenu}>
            {menuOpen ? '‚úï' : '‚ò∞'}
          </button>
          <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
            <a href="#profile" onClick={handleMenuClick}>Profile</a>
            <a href="#projects" onClick={handleMenuClick}>Projects</a>
            <a href="#about" onClick={handleMenuClick}>About</a>
            <a href="#contact" onClick={handleMenuClick}>Contact</a>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
