import React, { useState, useEffect } from 'react';
import '../mycss/MiddleSection.css';
import readhubImage from '../assets/readhub.png';
import vibetrackImage from '../assets/vibetrack.png';
import venues from '../assets/venues.png';
import event from '../assets/event.png';
import checkout from '../assets/checkout.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons';

function MiddleSection() {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleMenuClick = () => {
    setMenuOpen(false); // Close the menu when a menu item is clicked
  };

  const projectImages = [
    readhubImage,
    vibetrackImage,
    venues,
    event,
    checkout,
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentProjectIndex(prevIndex =>
        prevIndex === projectImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 2000);

    return () => clearInterval(intervalId);
  }, [projectImages.length]);

  return (
    <div className="middle-section">
      <div className="info-box">
        <a href="/info">Info</a>
      </div>
      <div className="projects-box">
        <div className='projects-carousel'>
          <img
            src={projectImages[currentProjectIndex]}
            alt={`Project ${currentProjectIndex + 1}`}
            width="500"
            height="600"
          />
        </div>
        <div className="view-projects">
          <a href="#projects" onClick={handleMenuClick} className="external-link">
            <span style={{color: 'green', fontSize: '30px'}}>View Projects</span> <FontAwesomeIcon icon={faExternalLinkAlt} color='white'/> 
          </a>
        </div>
      </div>
      <div className="contact-box">
        <a href="/contact">Contact</a>
      </div>
    </div>
  );
}

export default MiddleSection;
