import React, { useState, useEffect } from 'react';
import '../mycss/MiddleSection.css';
import readhubImage from '../assets/readhub.png';
import vibetrackImage from '../assets/vibetrack.png';
import venues from '../assets/venues.png';
import event from '../assets/event.png';
import ecommerce from '../assets/ecommerce.png'
import checkout from '../assets/checkout.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExternalLinkAlt, faGraduationCap } from '@fortawesome/free-solid-svg-icons';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

function MiddleSection() {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  const projectImages = [
    readhubImage,
    vibetrackImage,
    venues,
    event,
    checkout,
    ecommerce,
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentProjectIndex(prevIndex =>
        prevIndex === projectImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 2000);

    return () => clearInterval(intervalId);
  }, [projectImages.length]);

  return (
    <div className="middle-section">
      <div className="info-box">
        {/* <h1>Counting down the days until graduation! Stay tuned for a celebration to remember.</h1>
        <a href="/info">Info</a> */}
        <div className='up-box'>
          <p>Counting down the days until graduation! Stay tuned for a celebration to remember.</p>
        </div>
        <div className='down-box'>
          <img
            src={faGraduationCap}
            alt='draduation-memo'
          />
          <a href="/info">Info</a>
        </div>
      </div>

      <div className="projects-box">
        <div className='projects-carousel'>
          <img
            src={projectImages[currentProjectIndex]}
            alt={`Project ${currentProjectIndex + 1}`}
            width="100"
            height="200"
          />
          {/* <div className="view-projects">
            <a href="/projects" className="external-link">
              <span style={{color: 'deepskyblue', fontSize: '30px'}}>PROJECTS</span> <FontAwesomeIcon icon={faExternalLinkAlt} color='white'/> 
            </a>
          </div> */}
        </div>
        <div className="view-projects">
            <a href="/projects" className="external-link">
              <span style={{color: 'deepskyblue', fontSize: '30px'}}>PROJECTS</span> <FontAwesomeIcon icon={faExternalLinkAlt} color='gold'/> 
            </a>
          </div>
      </div>

      <div className="contact-box">
        <div className='contact-link'>
          <a href="/contact"> <FontAwesomeIcon icon={faArrowRight} className="arrow-icon" /></a>
        </div>
        <p>
          GET CONNECTED...<br />
          <br />
          <br />
          <br />
          <br />
          There is no doubt that code is becoming the language of the 21st century. 
          Get started today on the path toward the digital world, 
          the right investment for your future!
        </p>
      </div>
    </div>
  );
}

export default MiddleSection;
