import React, { useState, useEffect, useCallback } from 'react';
import '../mycss/MiddleSection.css';
import readhubImage from '../assets/readhub.png';
import vibetrackImage from '../assets/vibetrack.png';
import jay2Image from '../assets/jay2.png';

function MiddleSection() {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  const projectImages = [
    readhubImage,
    vibetrackImage,
    jay2Image
  ];

  const nextProject = useCallback(() => {
    setCurrentProjectIndex((prevIndex) =>
      prevIndex === projectImages.length - 1 ? 0 : prevIndex + 1
    );
  }, [projectImages.length]);

  useEffect(() => {
    const intervalId = setInterval(nextProject, 5000);

    return () => clearInterval(intervalId);
  }, [nextProject]);

  return (
    <div className="middle-section">
      <div className="info-box">
        <a href="/info">Info</a>
      </div>
      <div className="projects-box">
        <div className='projects-carousel'>
          <img
            src={projectImages[currentProjectIndex]}
            alt={`Project ${currentProjectIndex + 1}`}
          />
        </div>
        <div className="navigation">
          <button onClick={prevProject}>Previous</button>
          <button onClick={nextProject}>Next</button>
        </div>
        <div className="view-projects">
          <a href="/projects">View Projects</a>
        </div>
      </div>
      <div className="contact-box">
        <a href="/contact">Contact</a>
      </div>
    </div>
  );
}

export default MiddleSection;
