import React, { useState } from 'react';
import '../mycss/Header.css'; 
import logoImage from '../assets/jayce1.png';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleMenuClick = () => {
    setMenuOpen(false); // Close the menu when a menu item is clicked
  };

  return (
    <header>
      <div className="container">
        <img src={logoImage} alt="mylogo" width="150" />
        <div className="title">Jayce Turambe</div>
        <div className='hamburgermenu'>
          <button className='hamburger-button' onClick={toggleMenu}>
            {menuOpen ? '✕' : '☰'}
          </button>
          <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
            <a href="#profile" onClick={handleMenuClick}>Profile</a>
            <a href="#projects" onClick={handleMenuClick}>Projects</a>
            <a href="#about" onClick={handleMenuClick}>About</a>
            <a href="#contact" onClick={handleMenuClick}>Contact</a>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
