import React, {useState, useEffect} from 'react';
import '../mycss/MiddleSection.css';

function MiddleSection() {
  // State to track the current project image index
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  // Array of project images (replace with your project image URLs)
  const projectImages = [
    'assets/readhub.png',
    'assets/vibetrack.png',
    'assets/jay2.png',
    // Add more project image URLs as needed
  ];

  // Function to handle navigation to the next project image
  const nextProject = () => {
    setCurrentProjectIndex((prevIndex) =>
      prevIndex === projectImages.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Function to handle navigation to the previous project image
  const prevProject = () => {
    setCurrentProjectIndex((prevIndex) =>
      prevIndex === 0 ? projectImages.length - 1 : prevIndex - 1
    );
  };

  useEffect(() => {
    // Automatically move to the next project image after a certain interval (e.g., every 3 seconds)
    const intervalId = setInterval(nextProject, 3000);

    // Cleanup function to clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, [currentProjectIndex]); // Re-run effect whenever the currentProjectIndex changes



  return (
    <div className="middle-section">
      <div className="info-box">
        {/* Info Box Content */}
        <a href="/info">Info</a>
      </div>
      <div className="projects-box">
        {/* Projects Box Content */}
        <div className='projects-carousel'>
          <img
            src={projectImages[currentProjectIndex]}
            alt={`Project ${currentProjectIndex + 1}`}
          />
        </div>

        {/* Button/icon to navigate to projects page */}
        <div className="view-projects">
          <a href="/projects">View Projects</a>
        </div>
      </div>
      <div className="contact-box">
        {/* Contact Box Content */}
        <a href="/contact">Contact</a>
      </div>
    </div>
  );
}

export default MiddleSection;
